package com.esl.batch;

import com.esl.dao.IVocabImageDAO;
import com.esl.entity.VocabImage;
import com.mashape.unirest.http.HttpResponse;
import com.mashape.unirest.http.Unirest;
import org.apache.commons.codec.binary.StringUtils;
import org.apache.commons.io.IOUtils;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.context.support.ClassPathXmlApplicationContext;

import java.io.InputStream;
import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

public class AddVocabImageBatch {
	private static Logger log = LoggerFactory.getLogger(AddVocabImageBatch.class);

	public static void main(String[] args) {
		final String word = "sin";
		final List<String> urls = new ArrayList<>();
		urls.add("data:image/jpeg;base64,");
		urls.add("data:image/jpeg;base64,");
		urls.add("http://worldartsme.com/images/shifting-burden-clipart-1.jpg");

		ClassPathXmlApplicationContext ctx = new ClassPathXmlApplicationContext("/com/esl/ESL-context.xml");

		IVocabImageDAO dao = (IVocabImageDAO)ctx.getBean("vocabImageDAO");
		urls.stream()
				.map(AddVocabImageBatch::retrieveImageToString)
				.filter(o -> o.isPresent())
				.map(image -> new VocabImage(word,image.get()))
				.forEach(dao::persist);

		System.exit(0);
	}

	private static Optional<String> retrieveImageToString(String url) {
		log.debug("retrieveImageToString from url: {}", url);
		if (url.startsWith("data:image"))
			return Optional.of(url);

		try {
			String extension = url.substring(url.lastIndexOf('.') + 1);
			HttpResponse<InputStream> response = Unirest.get(url).asBinary();
			log.debug("Response status code: {}", response.getStatus());
			byte[] byteArray = IOUtils.toByteArray(response.getBody());
			return Optional.of("data:image/" + extension + ";base64," + StringUtils.newStringUtf8(org.apache.commons.codec.binary.Base64.encodeBase64(byteArray, false)));
		} catch (Exception e) {
			log.error("Cannot retrieve image from url: {}", url, e);
			return Optional.empty();
		}

	}
}